mapper.select:
SELECT
	c.line_name,
	DATE_FORMAT(check_time, "%Y-%m-%d") check_time,
	sum(a.ride_number) ride_number
FROM
	device_check_info a
INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no
LEFT JOIN line_management c ON a.subordinate_line = c.line_no

	  and a.check_time <= #{endTime}   
	  and a.check_time >= #{startTime} 
	 and a.subordinate_line = #{subordinateLine} 
	 and a.company_no = #{companyNo} 

GROUP BY
c.line_name,
DATE_FORMAT(CHECK_TIME, "%Y-%m-%d")

	limit #{offset}, #{limit}

SELECT count(*) FROM ( SELECT c.line_name, DATE_FORMAT(check_time, "%Y-%m-%d") check_time, sum(a.ride_number) ride_number FROM device_check_info a INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no LEFT JOIN line_management c ON a.subordinate_line = c.line_no
and a.check_time <= #{endTime} and a.check_time >= #{startTime} and a.subordinate_line = #{subordinateLine} and a.company_no = #{companyNo}
GROUP BY c.line_name, DATE_FORMAT(CHECK_TIME, "%Y-%m-%d") ) a

SELECT
sum(ride_number) totalNum
FROM
device_check_info a
INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no
LEFT JOIN line_management c ON a.subordinate_line = c.line_no

	  and a.check_time <= #{endTime}   
	  and a.check_time >= #{startTime} 
	 and a.subordinate_line = #{subordinateLine} 
	 and a.company_no = #{companyNo}

SELECT b.device_name, b.car_no, b.vehicle_number, DATE_FORMAT(check_time, "%Y-%m-%d") check_time, a.check_equipment_no, SUM(ride_number) ride_number FROM device_check_info a INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no
and a.check_time <= #{endTime} and a.check_time >= #{startTime} and a.check_equipment_no = #{checkEquipmentNo} and a.company_no = #{companyNo}
GROUP BY b.device_name, b.car_no, b.vehicle_number, DATE_FORMAT(check_time, "%Y-%m-%d"), a.check_equipment_no
limit #{offset}, #{limit}

SELECT count(*) FROM ( SELECT b.device_name, b.car_no, b.vehicle_number, DATE_FORMAT(check_time, "%Y-%m-%d") check_time, a.check_equipment_no, SUM(ride_number) ride_number FROM device_check_info a INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no
and a.check_time <= #{endTime} and a.check_time >= #{startTime} and a.check_equipment_no = #{checkEquipmentNo} and a.company_no = #{companyNo}
GROUP BY b.device_name, b.car_no, b.vehicle_number, DATE_FORMAT(check_time, "%Y-%m-%d"), a.check_equipment_no ) a

SELECT
sum(ride_number) totalNum
FROM
device_check_info a
INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no

	  and a.check_time <= #{endTime}   
	  and a.check_time >= #{startTime} 
	 and a.check_equipment_no = #{checkEquipmentNo} 
	 and a.company_no = #{companyNo}
