mapper.select:
		SELECT
			a.id,
			a.product_name,
			a.electronic_ticket,
			b.vehicle_number,
			a.ride_number,
			a.check_equipment_no,
			a.check_time,
			c.line_name,
			b.car_no
		FROM
		device_check_info a
		INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no
		LEFT JOIN line_management c ON a.subordinate_line = c.line_no

		  	 and a.electronic_ticket = #{electronicTicket} 
		  	 and a.order_distributor = #{orderDistributor} 
		  	 and a.check_equipment_no = #{checkEquipmentNo} 
		  	  and a.check_time <= #{endTime}   
			  and a.check_time >= #{startTime} 
		  	 and a.subordinate_line = #{subordinateLine} 
		  	 and a.ride_number = #{rideNumber} 
		  	 and a.product_name = #{productName} 
		  	 and a.company_no = #{companyNo} 
			 and b.vehicle_number = #{vehicleNumber} 



                order by ${sort} ${order}


                order by a.id desc



			limit #{offset}, #{limit}

select count(*) from device_check_info a
INNER JOIN device_car_equipment b ON a.check_equipment_no = b.device_no
LEFT JOIN line_management c ON a.subordinate_line = c.line_no

	  and a.electronic_ticket = #{electronicTicket} 
	  and a.order_distributor = #{orderDistributor} 
	  and a.check_equipment_no = #{checkEquipmentNo} 
	   and a.check_time <= #{endTime}   
	   and a.check_time >= #{startTime} 
	  and a.subordinate_line = #{subordinateLine} 
	  and a.ride_number = #{rideNumber} 
	  and a.product_name = #{productName} 
	  and a.company_no = #{companyNo} 
	  and b.vehicle_number = #{vehicleNumber}
