mapper.select:
SELECT a.id, `settlement_no`, b. NAME sell_user, `settlement_number`, `settlement_acoumt`, `settlement_user`, `settlement_time`, c.spot_name spot_no, `sell_ticket_time`, `refund_acoumt`, `receivable_amount`, `refund_num`, `settlement_status`, `settlement_review_person`, `settlement_review_time`, `review_amount`, a.company_no FROM fin_ticket_settlement a LEFT JOIN cooperation_distribution b ON a.sell_user = b.distribution_no LEFT JOIN base_spot_management c ON a.spot_no = c.spot_no
order by ${sort} ${order}
order by a.id desc
limit #{offset}, #{limit}

mapper.update:
update fin_ticket_settlement
`settlement_status` = #{settlementStatus}, `review_amount` = #{reviewAmount}, `settlement_review_person` = #{settlementReviewPerson}, `settlement_review_time` = #{settlementReviewTime}, `remarks` = #{remarks}
where settlement_no = #{settlementNo} and company_no = #{companyNo}

update order_spot

	`order_status` = #{orderStatus},
	`whether_settlement` = #{settlementStatus}


where settlement_no = #{settlementNo} and company_no = #{companyNo}

sql.where:
a.company_no = #{companyNo}
and a.settlement_status = #{settlementStatus}

	and (
	a.settlement_no LIKE
	CONCAT(CONCAT('', #{searchName}),'%')
	)

 and a.sell_user = #{sellUser} 
 and a.settlement_time = #{settlementTime} 
 and a.spot_no = #{spotNo}
