mapper.select:
select `product_id`,`ticket_no`,`product_no`,base_url,is_entity,`term_no`,`product_name`,`distribution_channel`,`pay_type`,`pay_price`,`order_cancel`,`whether_user_phone`,`whether_user_id`,`whether_make`,`certificate_exchange`,`park_way`,`park_address`,`tick_type`,`use_explain`,`whether_refund`,`whether_examine`,`refund_explain`,`cost_includes`,`cost_not_includes`,`whether_user_name`,`whether_shelves`,`template_sms_no`,`template_mms_no`,`settlement_price`,`ticket_grouping`,`effective_days`,`whether_shelves_advance`,`advance_start_date`,`advance_end_date`,`user_role_id`,`template_no`,`img_url`,`whether_sure_ride`,`subordinate_line`,`whether_refund_strategy`,`strategy_no`,`refund_strategy_detail_no`,`whether_limit_sell`,`sell_date_type`,`limit_sell_num`,`product_provider`,`car_fee`,`product_type`,`company_no`,`whether_limit_stock`,`product_second_type`,product_type_name,sort,whether_real_name from product_spot_ticket

    (
        product_name LIKE
        CONCAT(CONCAT('', #{searchName}),'%')
        )

     and product_id = #{productId} 
     and ticket_no = #{ticketNo} 
     and product_no = #{productNo} 
     and term_no = #{termNo} 
     and product_name = #{productName} 
     and distribution_channel = #{distributionChannel} 
     and pay_type = #{payType} 
     and pay_price = #{payPrice} 
     and order_cancel = #{orderCancel} 
     and whether_user_phone = #{whetherUserPhone} 
     and whether_user_id = #{whetherUserId} 
     and whether_make = #{whetherMake} 
     and certificate_exchange = #{certificateExchange} 
     and park_way = #{parkWay} 
     and park_address = #{parkAddress} 
     and tick_type = #{tickType} 
     and use_explain = #{useExplain} 
     and whether_refund = #{whetherRefund} 
     and whether_examine = #{whetherExamine} 
     and refund_explain = #{refundExplain} 
     and cost_includes = #{costIncludes} 
     and cost_not_includes = #{costNotIncludes} 
     and whether_user_name = #{whetherUserName} 
     and whether_shelves = #{whetherShelves} 
     and template_sms_no = #{templateSmsNo} 
     and template_mms_no = #{templateMmsNo} 
     and settlement_price = #{settlementPrice} 
     and ticket_grouping = #{ticketGrouping} 
     and effective_days = #{effectiveDays} 
     and whether_shelves_advance = #{whetherShelvesAdvance} 
     and advance_start_date = #{advanceStartDate} 
     and advance_end_date = #{advanceEndDate} 
     and user_role_id = #{userRoleId} 
     and template_no = #{templateNo} 
     and img_url = #{imgUrl} 
     and whether_sure_ride = #{whetherSureRide} 
     and subordinate_line = #{subordinateLine} 
     and whether_refund_strategy = #{whetherRefundStrategy} 
     and strategy_no = #{strategyNo} 
     and refund_strategy_detail_no = #{refundStrategyDetailNo} 
     and whether_limit_sell = #{whetherLimitSell} 
     and sell_date_type = #{sellDateType} 
     and limit_sell_num = #{limitSellNum} 
     and product_provider = #{productProvider} 
     and car_fee = #{carFee} 
     and product_type = #{productType} 
     and company_no = #{companyNo} 
     and whether_limit_stock = #{whetherLimitStock} 
     and product_second_type = #{productSecondType} 
     and is_entity = #{isEntity} 



        order by ${sort} ${order}


        order by product_id desc



    limit #{offset}, #{limit}

