mapper.select:
		SELECT
		`ticket_id`,
		a.spot_no,
		c.spot_name,
		`ticket_no`,
		is_self,
		`ticket_name`,
		`ticket_supplier`,
		`ticket_type`,
		`ticket_price`,
		`after_take_scene_refund`,
		`expired_refund`,
		`whether_shelves`,
		a.create_user,
		a.create_time,
		a.update_user,
		a.update_time,
		`abbreviation`,
		b.product_type_name product_type,
		a.company_no,
		(
		SELECT
		supplier_name
		FROM
		base_supplier s
		WHERE
		s.supplier_no = a.ticket_supplier
		LIMIT 1
		) AS supplier_name
		FROM
		base_ticket_type a
		LEFT JOIN product_type b ON a.product_type = b.product_type_no
		LEFT JOIN base_spot_management c ON c.spot_no = a.spot_no

			a.company_no = #{companyNo}

                and (a.ticket_no LIKE
                CONCAT(CONCAT(#{searchName}),'%')
                or
				a.ticket_name
                LIKE CONCAT(CONCAT(#{searchName}),'%')
                or
				a.spot_name LIKE CONCAT(CONCAT( #{searchName}),'%')
                or
				a.ticket_price LIKE CONCAT(CONCAT(#{searchName}),'%')
                )

		  		   and a.ticket_id = #{ticketId} 
		  		   and a.spot_no = #{spotNo} 
		  		   and a.spot_name = #{spotName} 
		  		   and a.ticket_no = #{ticketNo} 
		  		   and a.ticket_name = #{ticketName} 
		  		   and a.ticket_supplier = #{ticketSupplier} 
		  		   and a.ticket_type = #{ticketType} 
		  		   and a.ticket_price = #{ticketPrice} 
		  		   and a.after_take_scene_refund = #{afterTakeSceneRefund} 
		  		   and a.expired_refund = #{expiredRefund} 
		  		   and a.whether_shelves = #{whetherShelves} 
		  		   and a.create_user = #{createUser} 
		  		   and a.create_time = #{createTime} 
		  		   and a.update_user = #{updateUser} 
		  		   and a.update_time = #{updateTime} 
		  		   and a.abbreviation = #{abbreviation} 
		  		   and a.product_type = #{productType} 
		  		   and a.is_self = #{isSelf} 



                order by ${sort} ${order}


                order by a.ticket_id desc



			limit #{offset}, #{limit}

select count(*) from base_ticket_type a
LEFT  JOIN product_type b ON
a.product_type = b.product_type_no

	a.company_no = #{companyNo}

		and (a.ticket_no LIKE
		CONCAT(CONCAT(#{searchName}),'%')
		or
		a.ticket_name
		LIKE CONCAT(CONCAT(#{searchName}),'%')
		or
		a.spot_name LIKE CONCAT(CONCAT( #{searchName}),'%')
		or
		a.ticket_price LIKE CONCAT(CONCAT(#{searchName}),'%')
		)

	 and a.ticket_id = #{ticketId} 
	 and a.spot_no = #{spotNo} 
	 and a.spot_name = #{spotName} 
	 and a.ticket_no = #{ticketNo} 
	 and a.ticket_name = #{ticketName} 
	 and a.ticket_supplier = #{ticketSupplier} 
	 and a.ticket_type = #{ticketType} 
	 and a.ticket_price = #{ticketPrice} 
	 and a.after_take_scene_refund = #{afterTakeSceneRefund} 
	 and a.expired_refund = #{expiredRefund} 
	 and a.whether_shelves = #{whetherShelves} 
	 and a.create_user = #{createUser} 
	 and a.create_time = #{createTime} 
	 and a.update_user = #{updateUser} 
	 and a.update_time = #{updateTime} 
	 and a.abbreviation = #{abbreviation} 
	 and a.product_type = #{productType} 
	 and a.is_self = #{isSelf}
