mapper.select:
		select ,
		p.product_name,CONCAT(p.base_url,p.img_url) as product_img,a.nick_name,a.avatar
		from shopping_cart s
		LEFT JOIN product_spot_ticket p
		on s.product_no=p.product_no
		LEFT JOIN application_user a on s.open_id=a.openId
          s.status='1'

					  and (p.product_name LIKE
					  CONCAT(CONCAT('', #{searchName}),'%')
					  or
					  a.nick_name
					  LIKE CONCAT(CONCAT('', #{searchName}),'%')
					  or
					  s.id_card
					  LIKE CONCAT(CONCAT('', #{searchName}),'%')
					  or
					  s.customer_name
					  LIKE CONCAT(CONCAT('', #{searchName}),'%')
					  or
					  s.product_no=#{searchName})

		  		   and id = #{id} 
		  		   and open_id = #{openId} 
		  		   and product_no = #{productNo} 
		  		   and product_num = #{productNum} 
		  		   and customer_name = #{customerName} 
		  		   and id_card = #{idCard} 
		  		   and phone_num = #{phoneNum} 
		  		   and play_time = #{playTime} 
		  		   and sort = #{sort} 
		  		   and checked = #{checked} 
		  		   and create_time = #{createTime} 
		  		   and update_time = #{updateTime} 
		  		   and application_no = #{applicationNo} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from shopping_cart s
LEFT JOIN product_spot_ticket p
on s.product_no=p.product_no
LEFT JOIN application_user a on s.open_id=a.openId
  s.status='1'

		and (p.product_name LIKE
		CONCAT(CONCAT('', #{searchName}),'%')
		or
		a.nick_name
		LIKE CONCAT(CONCAT('', #{searchName}),'%')
		or
		s.id_card
		LIKE CONCAT(CONCAT('', #{searchName}),'%')
		or
		s.customer_name
		LIKE CONCAT(CONCAT('', #{searchName}),'%')
		or
		s.product_no=#{searchName})

	 and s.id = #{id} 
	 and s.open_id = #{openId} 
	 and s.product_no = #{productNo} 
	 and s.product_num = #{productNum} 
	 and s.customer_name = #{customerName} 
	 and s.id_card = #{idCard} 
	 and s.phone_num = #{phoneNum} 
	 and s.play_time = #{playTime} 
	 and s.sort = #{sort} 
	 and s.checked = #{checked} 
	 and s.create_time = #{createTime} 
	 and s.update_time = #{updateTime} 
	 and s.application_no = #{applicationNo}

mapper.update:
update shopping_cart
open_id = #{openId}, product_no = #{productNo}, product_num = #{productNum}, customer_name = #{customerName}, id_card = #{idCard}, phone_num = #{phoneNum}, play_time = #{playTime}, status = #{status}, sort = #{sort}, checked = #{checked}, create_time = #{createTime}, update_time = #{updateTime}, application_no = #{applicationNo}
where id = #{id}
