mapper.select:
		select id,mch_no,provider_no,
		(select provider_name from payment_wechat_provider w where w.provider_no=p.provider_no limit 1) as providerName,
		mch_name,private_key,mch_id,cert_password,bz,available,cert_local_path
		,create_time,update_time,create_user,update_user,company_no from payment_wechat_mch p

		  		   and id = #{id} 
		  		   and mch_no = #{mchNo} 
		  		   and provider_no = #{providerNo} 
		  		   and mch_name = #{mchName} 
		  		   and private_key = #{privateKey} 
		  		   and mch_id = #{mchId} 
		  		   and cert_local_path = #{certLocalPath} 
		  		   and cert_password = #{certPassword} 
		  		   and bz = #{bz} 
		  		   and available = #{available} 
		  		   and create_time = #{createTime} 
		  		   and update_time = #{updateTime} 
		  		   and create_user = #{createUser} 
		  		   and update_user = #{updateUser} 
		  		   and company_no = #{companyNo} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from payment_wechat_mch

  		   and id = #{id} 
  		   and mch_no = #{mchNo} 
  		   and provider_no = #{providerNo} 
  		   and mch_name = #{mchName} 
  		   and private_key = #{privateKey} 
  		   and mch_id = #{mchId} 
  		   and cert_local_path = #{certLocalPath} 
  		   and cert_password = #{certPassword} 
  		   and bz = #{bz} 
  		   and available = #{available} 
  		   and create_time = #{createTime} 
  		   and update_time = #{updateTime} 
  		   and create_user = #{createUser} 
  		   and update_user = #{updateUser} 
  		   and company_no = #{companyNo}

mapper.update:
update payment_wechat_mch
mch_no = #{mchNo}, provider_no = #{providerNo}, mch_name = #{mchName}, private_key = #{privateKey}, mch_id = #{mchId}, cert_local_path = #{certLocalPath}, cert_password = #{certPassword}, bz = #{bz}, available = #{available}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser}, company_no = #{companyNo} base_url = #{baseUrl}
where id = #{id}
