mapper.select:
		select id,provider_no,provider_name,app_id,private_key,mch_id
		,cert_password,bz,available,create_time
		,update_time,create_user,update_user,company_no,cert_local_path
		from payment_wechat_provider

		  		   and id = #{id} 
		  		   and provider_no = #{providerNo} 
		  		   and provider_name = #{providerName} 
		  		   and app_id = #{appId} 
		  		   and private_key = #{privateKey} 
		  		   and mch_id = #{mchId} 
		  		   and cert_local_path = #{certLocalPath} 
		  		   and cert_password = #{certPassword} 
		  		   and bz = #{bz} 
		  		   and available = #{available} 
		  		   and create_time = #{createTime} 
		  		   and update_time = #{updateTime} 
		  		   and create_user = #{createUser} 
		  		   and update_user = #{updateUser} 
		  		   and company_no = #{companyNo} 



                order by ${sort} ${order}


                order by id desc



			limit #{offset}, #{limit}

select count(*) from payment_wechat_provider

  		   and id = #{id} 
  		   and provider_no = #{providerNo} 
  		   and provider_name = #{providerName} 
  		   and app_id = #{appId} 
  		   and private_key = #{privateKey} 
  		   and mch_id = #{mchId} 
  		   and cert_local_path = #{certLocalPath} 
  		   and cert_password = #{certPassword} 
  		   and bz = #{bz} 
  		   and available = #{available} 
  		   and create_time = #{createTime} 
  		   and update_time = #{updateTime} 
  		   and create_user = #{createUser} 
  		   and update_user = #{updateUser} 
  		   and company_no = #{companyNo}

mapper.update:
update payment_wechat_provider
provider_no = #{providerNo}, provider_name = #{providerName}, app_id = #{appId}, private_key = #{privateKey}, mch_id = #{mchId}, cert_local_path = #{certLocalPath}, cert_password = #{certPassword}, bz = #{bz}, available = #{available}, create_time = #{createTime}, update_time = #{updateTime}, create_user = #{createUser}, update_user = #{updateUser}, company_no = #{companyNo} base_url = #{baseUrl}
where id = #{id}
